    private void jTextFieldNomeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jTextFieldNomeActionPerformed
        // TODO add your handling code here:
    }//GEN-LAST:event_jTextFieldNomeActionPerformed
      int i  = 0;
    private void jButtonAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAddActionPerformed
      if(jComboBoxProf.getModel().getSelectedItem().toString().equals("Professor")){
          Professor professor = new Professor();
          professor.setNome(jTextFieldNome.getText());
          professor.setProfissao(jComboBoxProf.getSelectedItem().toString());
          
          jTable2.setValueAt(professor.getNome(), i, 0);
          jTable2.setValueAt(professor.getProfissao(), i, 1);
          jTable2.setValueAt(professor.ensinar(), i, 2);
      }else{
          Aluno aluno = new Aluno();
          aluno.setNome(jTextFieldNome.getText());
          aluno.setProfissao(jComboBoxProf.getSelectedItem().toString());
          
          jTable2.setValueAt(aluno.getNome(), i, 0);
          jTable2.setValueAt(aluno.getProfissao(), i, 1);
          jTable2.setValueAt(aluno.aprender(), i, 2);
      }
      
      i++;
        
    }//GEN-LAST:event_jButtonAddActionPerformed

